mapper.select:
SELECT DISTINCT s.`object_id` FROM `street` s JOIN `street_attribute` a_name ON (s.`object_id` = a_name.`object_id` AND a_name.`status` = 1 AND a_name.`attribute_type_id` = ${streetNameAT}) JOIN `street_attribute` a_type ON (s.`object_id` = a_type.`object_id` AND a_type.`status` = 1 AND a_type.`attribute_type_id` = ${streetTypeAT}) JOIN `street_string_value` sc ON (a_name.`value_id` = sc.`id` AND sc.`locale_id` = ${localeId}) WHERE (s.`status` IN (1, 0)) AND sc.`value` = #{text} AND a_type.`value_id` = #{streetTypeId}
AND s.`parent_entity_id` = #{parentEntityId} AND s.`parent_id` = #{parentId}
AND s.`parent_entity_id` IS NULL AND s.`parent_id` IS NULL

select distinct s.`object_id` from `street` s
  join `street_attribute` a_st on (a_st.`status` = 1
    and s.`object_id` = a_st.`object_id` and a_st.`value_type_id` = 301)
  join `street_attribute` a on (a.`status` = 1
    and s.`object_id` = a.`object_id` and a.`value_type_id` = 300)
  join `street_string_value` sc on (sc.`id` = a.`value_id`)
where s.`status` = 1 and s.`parent_entity_id` = 400
  and s.`parent_id` = #{cityId}
  and a_st.`value_id` = #{streetTypeId}
  and sc.`value` = #{streetName}

SELECT DISTINCT s.`object_id` FROM `street` s JOIN `street_attribute` street_attr_name ON (street_attr_name.`status` = 1 AND s.`object_id` = street_attr_name.`object_id` AND street_attr_name.`attribute_type_id` = 300) JOIN `street_string_value` street_name ON (street_name.`id` = street_attr_name.`value_id` AND street_name.`value` = #{street}) JOIN `building_address` addr ON (addr.`status` = 1 AND addr.`parent_id` = s.`object_id` AND addr.`parent_entity_id` = 300) JOIN `building_address_attribute` num_attr ON (num_attr.`status` = 1 AND num_attr.`object_id` = addr.`object_id` AND num_attr.`attribute_type_id` = 1500) JOIN `building_address_string_value` num ON (num.`id` = num_attr.`value_id` AND num.`value` = #{number}) LEFT JOIN `building_address_attribute` corp_attr ON (corp_attr.`status` = 1 AND corp_attr.`object_id` = addr.`object_id` AND corp_attr.`attribute_type_id` = 1501) LEFT JOIN `building_address_string_value` corp ON (corp.`id` = corp_attr.`value_id`) WHERE s.`status` = 1 AND s.`parent_id` = #{cityId} AND s.`parent_entity_id` = 400 AND
corp.`value` IS NULL
corp.`value` = #{corp}
