sql.if:
AND (EXISTS(
        SELECT 1 FROM "building" b
            JOIN "building_attribute" district_attr ON (b."object_id" = district_attr."object_id")
        WHERE b."status" IN (1, 0) AND b."parent_id" = e."object_id"  AND district_attr."status" = 1
            AND district_attr."entity_attribute_id" = 503 AND district_attr."value_id" = #{additionalParams.district}
        )
)

mapper.select:
SELECT DISTINCT s."object_id" FROM "street" s JOIN "street_attribute" a_name ON (s."object_id" = a_name."object_id" AND a_name."status" = 1 AND a_name."entity_attribute_id" = ${streetNameAT}) JOIN "street_attribute" a_type ON (s."object_id" = a_type."object_id" AND a_type."status" = 1 AND a_type."entity_attribute_id" = ${streetTypeAT}) JOIN "street_string_value" sc ON (a_name."value_id" = sc."id" AND sc."locale_id" = ${localeId}) WHERE (s."status" IN (1, 0)) AND sc."value" = #{text} AND a_type."value_id" = #{streetTypeId}
AND s."parent_entity_id" = #{parentEntityId} AND s."parent_id" = #{parentId}
AND s."parent_entity_id" IS NULL AND s."parent_id" IS NULL

SELECT DISTINCT s."object_id" FROM "street" s
JOIN "street_attribute" a_st ON (a_st."status" = 1
    AND s."object_id" = a_st."object_id" AND a_st."entity_attribute_id" = 301)
JOIN "street_attribute" a ON (a."status" = 1
    AND s."object_id" = a."object_id" AND a."entity_attribute_id" = 300)
JOIN "street_string_value" sc ON (sc."id" = a."value_id")
WHERE s."status" = 1 AND s."parent_entity_id" = 400
    AND s."parent_id" = #{cityId}
    AND a_st."value_id" = #{streetTypeId}
    AND sc."value" = #{streetName}

SELECT DISTINCT s."object_id" FROM "street" s JOIN "street_attribute" street_attr_name ON (street_attr_name."status" = 1 AND s."object_id" = street_attr_name."object_id" AND street_attr_name."entity_attribute_id" = 300) JOIN "street_string_value" street_name ON (street_name."id" = street_attr_name."value_id" AND street_name."value" = #{street}) JOIN "building" b ON (b."status" = 1 AND b."parent_id" = s."object_id" AND b."parent_entity_id" = 300) JOIN "building_attribute" num_attr ON (num_attr."status" = 1 AND num_attr."object_id" = b."object_id" AND num_attr."entity_attribute_id" = 500) JOIN "building_string_value" num ON (num."id" = num_attr."value_id" AND num."value" = #{number}) LEFT JOIN "building_attribute" corp_attr ON (corp_attr."status" = 1 AND corp_attr."object_id" = b."object_id" AND corp_attr."entity_attribute_id" = 501) LEFT JOIN "building_string_value" corp ON (corp."id" = corp_attr."value_id") WHERE s."status" = 1 AND s."parent_id" = #{cityId} AND s."parent_entity_id" = 400 AND
corp."value" IS NULL
corp."value" = #{corp}
