sql.where:
g.group_type = 1
AND (p."id" is not null or b."id" is not null)
AND g."id" = #{id}
AND YEAR(p."loaded") = YEAR(#{loaded}) AND DAYOFYEAR(p."loaded") = DAYOFYEAR(#{loaded})
AND p."directory" LIKE CONCAT('%',#{directory},'%')
AND p."name" LIKE CONCAT('%',#{paymentName},'%')
AND b."name" LIKE CONCAT('%',#{benefitName},'%')
AND p."organization_id" = #{organizationId}
AND p."user_organization_id" = #{userOrganizationId}
AND YEAR(p."begin_date") = #{year}
AND MONTH(p."begin_date") = #{month}
AND g."status" = #{status}

    AND p."name" LIKE concat('%', #{edrpou}, '%')
